import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { URL_CUS } from '../url';
import Add_cus from './add_cus';  // Assuming your file is named 'add_cus'

const Coustomer = ({ triggerUpdate }) => {
  const [cuslist, setCuslist] = useState([]);

  useEffect(() => {
    cus_get_list();
  }, [triggerUpdate]); // Trigger the effect when triggerUpdate changes

  const cus_get_list = async () => {
    try {
      const res = await axios.get(URL_CUS);
      setCuslist(res.data);
    } catch (error) {
      console.error('Error fetching customer list:', error);
    }
  };

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>S.No</th>
            <th>Name</th>
            <th>Phone</th>
            <th>Pending Flag</th>
            <th>Views</th>
          </tr>
        </thead>
        <tbody>
          {cuslist.map((customer, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{customer.name}</td>
              <td>{customer.pone}</td>
              <td>{customer.pending_flag}</td>
              <td>
                <a href="/views">Views</a>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Coustomer;
