import { useForm, useFieldArray } from 'react-hook-form';

const Add_chit =()=>{
    const { register, control, handleSubmit,reset,setValue} =useForm({
        defaultValues:{
            chit_det: { chit_name: '', Tolal_amount: '',Due_amount:'', time_preiod: '' },
            member_list:[{name:''}]
        }
        
    })
    const { fields, append, remove } = useFieldArray({
        control,
        name: 'member_list',
      });
      const onAddProduct = () => {
        append({ name: ''});
      };
  
  const onSubmit=()=>{

  }

  return(
    <form onSubmit={handleSubmit(onSubmit)}>
      <div className='row' >
        <div className='col-6'>
          <div>
            <label>Chit Name:</label>
            <input {...register('chit_det.chit_name')} placeholder="Customer Name" />
          </div>
          <div>
            <label>Email:</label>
            <input {...register('chit_det.Tolal_amount')} placeholder="Email" type="email" />
          </div>
          <div>
            <label>Address:</label>
            <input {...register('chit_det.Due_amount')} placeholder="Address" />
          </div>
          <div>
            <label>Address:</label>
            <input {...register('chit_det.time_preiod')} placeholder="Address" />
          </div>
        </div>
      </div>
      <hr></hr>
      <br></br> 
      <table>
        
      <tbody>
        <tr>
      {fields.map((item, index) => (
        
        <div key={item.id}>
          <td>
          <input
            {...register(`member_list.${index}.name`)}
            placeholder="Product Name"
          />
          </td>
          <td>
          <button type="button" onClick={() => remove(index)}>
            Remove
          </button>
          </td>
          
        </div>
      ))}
      </tr>
      </tbody>
      </table>
      <button type="button" onClick={onAddProduct}>
        Add Product
      </button>
      <button type="submit">Submit</button>      
    </form>
  )
}
export  default Add_chit
