import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  //   chit_get_url,
  //   chitdue_get_url,
  //   chitdue_add_url,
  //   chitdue_amount_update_url,
  dueamountunwind_url,
} from "./url/url";

const Chit = () => {
  const [chitduememberlist, setChitduememberlist] = useState([]);
  const [filteredChitduememberlist, setFilteredChitduememberlist] = useState(
    []
  );
  const [filters, setFilters] = useState({
    name: "",
    chitName: "",
    date: "",
    pendingFlag: "",
  });

  useEffect(() => {
    getchitduememberlist();
  }, []);

  useEffect(() => {
    applyFilters();
  }, [chitduememberlist, filters]);

  const getchitduememberlist = async () => {
    try {
      const response = await axios.get(dueamountunwind_url);
      setChitduememberlist(response.data);
    } catch (error) {
      console.error("Error fetching chit master list:", error);
    }
  };

  const applyFilters = () => {
    let filteredList = chitduememberlist.filter((member) => {
      return (
        member.member.name.toLowerCase().includes(filters.name.toLowerCase()) &&
        member.chit_name
          .toLowerCase()
          .includes(filters.chitName.toLowerCase()) &&
        member.date.includes(filters.date) &&
        (filters.pendingFlag === "" ||
          member.member.pending_flag === (filters.pendingFlag === "Yes"))
      );
    });
    setFilteredChitduememberlist(filteredList);
  };

  const handlePaidAmountChange = async (memberId, newPaidAmount) => {
    try {
      // Assuming you have an API endpoint to update the paid amount
      await axios.put(`/api/members/${memberId}/updatePaidAmount`, {
        paidAmount: newPaidAmount,
      });
      // Optionally, you can update the local state with the new data from the backend
      // getchitduememberlist();
    } catch (error) {
      console.error("Error updating paid amount:", error);
    }
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters({ ...filters, [name]: value });
  };

  return (
    <div>
      <h5>Due List</h5>
      <div>
        <input
          type="text"
          name="name"
          placeholder="Filter by Name"
          value={filters.name}
          onChange={handleFilterChange}
        />
        <input
          type="text"
          name="chitName"
          placeholder="Filter by Chit Name"
          value={filters.chitName}
          onChange={handleFilterChange}
        />
        <input
          type="text"
          name="date"
          placeholder="Filter by Date"
          value={filters.date}
          onChange={handleFilterChange}
        />
        <select
          name="pendingFlag"
          onChange={handleFilterChange}
          value={filters.pendingFlag}
        >
          <option value="">All</option>
          <option value="Yes">Pending</option>
          <option value="No">Not Pending</option>
        </select>
      </div>
      <table>
        <thead>
          <tr>
            <th>Member Name</th>
            <th>Chit Name</th>
            <th>Chit List</th>
            <th>Date</th>
            <th>Amount</th>
            <th>Due Amount</th>
            <th>Pending Flag</th>
            <th>Paid Amount</th>
          </tr>
        </thead>
        <tbody>
          {filteredChitduememberlist.map((member, index) => (
            <tr key={index}>
              <td>{member.member.name}</td>
              <td>{member.chit_name}</td>
              <td>{member.chit_list}</td>
              <td>{member.date}</td>
              <td>{member.amount}</td>
              <td>{member.member.due_amount}</td>
              <td>{member.member.pending_flag ? "Yes" : "No"}</td>
              <td>
                <input
                  type="text"
                  value={member.member.paidAmount}
                  onChange={(e) =>
                    handlePaidAmountChange(
                      member.member.member_id,
                      e.target.value
                    )
                  }
                  style={{
                    width: "150px",
                    backgroundColor: member.member.due_amount
                      ? "lightgreen"
                      : "inherit",
                    pointerEvents: member.member.due_amount ? "none" : "auto",
                  }}
                  readOnly={member.member.due_amount ? true : false}
                  placeholder={member.member.due_amount}
                />
              </td>
              <td>
                <button
                  onClick={() =>
                    handleUpdate(member.member_id, PaidAmount, chitDue._id)
                  }
                >
                  update
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Chit;
