import axios from "axios";
import { useForm, useFieldArray } from "react-hook-form";
import { chit_get_url, chit_add_url, customer_get_url } from "./url/url";
import { useEffect, useState } from "react";
import { Button, Modal } from "react-bootstrap";

const AddChitModal = () => {
  const [showModal, setShowModal] = useState(false);

  const handleClose = () => setShowModal(false);
  const handleShow = () => setShowModal(true);

  return (
    <>
      <Button variant="primary" onClick={handleShow}>
        Add Chit
      </Button>

      <Modal show={showModal} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Add Chit</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <AddChitForm closeModal={handleClose} />
        </Modal.Body>
      </Modal>
    </>
  );
};

const AddChitForm = ({ closeModal }) => {
  const { register, control, handleSubmit } = useForm({
    defaultValues: {
      chit_det: {
        chit_name: "",
        Tolal_amount: "",
        Due_amount: "",
        time_preiod: "",
      },
      member_list: [{ name: "" }],
    },
  });

  const { fields, append, remove } = useFieldArray({
    control,
    name: "member_list",
  });

  const [customerList, setCustomerList] = useState([]);

  useEffect(() => {
    async function fetchData() {
      try {
        const res = await axios.get(customer_get_url);
        setCustomerList(res.data);
      } catch (error) {
        console.error("Error fetching customer list:", error);
      }
    }
    fetchData();
  }, []);

  const onSubmit = async (data) => {
    try {
      const requestData = {
        chit_det: data.chit_det,
        member_list: data.member_list,
      };
      const response = await axios.post(chit_add_url, requestData);
      console.log("Server Response:", response.data);
      closeModal();
    } catch (error) {
      console.error("Error while submitting data:", error);
    }
  };

  const onAddProduct = () => {
    append({ name: "" });
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <div className="row">
        <div className="col-6">
          <div>
            <label>Chit Name:</label>
            <input
              {...register("chit_det.chit_name")}
              placeholder="Chit Name"
            />
          </div>
          <div>
            <label>Chit Amount:</label>
            <input
              {...register("chit_det.Tolal_amount")}
              placeholder="Total Amount"
            />
          </div>
          <div>
            <label>Due Amount:</label>
            <input
              {...register("chit_det.Due_amount")}
              placeholder="Due Amount"
            />
          </div>
          <div>
            <label>Time Period:</label>
            <input
              {...register("chit_det.time_preiod")}
              placeholder="Time Period"
            />
          </div>
        </div>

        <div className="col-6">
          <table>
            <tbody>
              {fields.map((item, index) => (
                <tr key={item.id}>
                  <td>
                    <input
                      {...register(`member_list.${index}.name`)}
                      placeholder="Member Name"
                      list={`datalist-${index}`}
                    />
                    <datalist id={`datalist-${index}`}>
                      {customerList.map((option, optionIndex) => (
                        <option key={optionIndex} value={option.name}>
                          {option.name}
                        </option>
                      ))}
                    </datalist>
                  </td>
                  <td>
                    <button type="button" onClick={() => remove(index)}>
                      Remove
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <button type="button" onClick={onAddProduct}>
            Add Member
          </button>
        </div>
      </div>
      <button type="submit">Submit</button>
    </form>
  );
};

export default AddChitModal;
