import axios from "axios";
import React, { useEffect, useState } from "react";
import { URL_CUS } from "../url";

const Coustomer = () => {
  const [cuslist, setCuslist] = useState([]);
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [pending_flag, setPending_flag] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    setName("");
    setPhone("");
    setPending_flag("");
    await addCusPost();
    cusGetList();
  };

  const addCusPost = async () => {
    const data = {
      name: name,
      phone: phone,
      pending_flag: pending_flag,
    };
    const newCustomer = new Customer(data);
    await newCustomer.save();
  };

  const cusGetList = async () => {
    try {
      const res = await axios.get(URL_CUS);
      console.log(res.data);
      setCuslist(res.data);
    } catch (error) {
      console.error("Error fetching customer list:", error);
    }
  };

  useEffect(() => {
    cusGetList();
  }, []);

  return (
    <div>
      <form
        onSubmit={handleSubmit}
        style={{ display: "flex", flexDirection: "row" }}
      >
        <input
          type="text"
          required
          onChange={(e) => setName(e.target.value)}
          placeholder="name"
        />
        <input
          type="phone"
          required
          onChange={(e) => setPhone(e.target.value)}
          placeholder="phone"
        />
        <label>
          Pending Flag:
          <input
            type="checkbox"
            onChange={(e) => setPending_flag(e.target.checked)}
          />
        </label>
        <button>Add</button>
      </form>
      <table>
        <thead>
          <tr>
            <th>S.No</th>
            <th>Name</th>
            <th>Phone</th>
            <th>Pending Flag</th>
            <th>Views</th>
          </tr>
        </thead>
        <tbody>
          {cuslist.map((customer, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{customer.name}</td>
              <td>{customer.phone}</td>
              <td>{customer.pending_flag ? "Yes" : "No"}</td>
              <td>
                <a href={`/views/${customer._id}`}>Views</a>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Coustomer;
