import axios from "axios";
import React, { useEffect, useState } from "react";
import { chit_get_url, chitdue_get_url } from "./url/url";

const ChitMaster = () => {
  const [chitMasterList, setChitMasterList] = useState([]);
  const [chitList, setChitList] = useState([]);
  const [selectedChit, setSelectedChit] = useState("");
  const [date, setDate] = useState("");
  const [amount, setAmount] = useState("");
  const [ChitDue, SetChitDue] = useState({
    defaultValues: {
      chitDueDet: { dueList: "", name: "", date: "", amount: "" },
      members: [
        { name: "", chit_det: "", date: "", amount: "", pending_flag: "" },
      ],
    },
  });
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.get();

      const dueLists = [];
      const chit_det = { chit_name: selectedChit, date: date, amount: amount };

      response.data.forEach((item) => {
        item.member_list.forEach((member) => {
          const dueList = {
            chit_det: chit_det,
            name: member.name,
            date: date,
            amount: amount,
            pending_flag: "true",
          };
          dueLists.push(dueList);
        });
      });
    } catch (error) {
      console.error("Error fetching chit master list:", error);
    }
  };

  const chitlistget = async () => {
    try {
      const response = await axios.get(chit_get_url);
      setChitList(response.data);
    } catch (error) {
      console.error("Error fetching chit list:", error);
    }
  };

  useEffect(() => {
    getChitMasterList();
    chitlistget();
  }, []);

  const getChitMasterList = async () => {
    try {
      const response = await axios.get(chitdue_get_url);
      setChitMasterList(response.data);
    } catch (error) {
      console.error("Error fetching chit master list:", error);
    }
  };

  return (
    <div>
      <form
        onSubmit={handleSubmit}
        style={{ display: "flex", flexDirection: "row" }}
      >
        <select
          value={selectedChit}
          onChange={(e) => setSelectedChit(e.target.value)}
        >
          <option value="">Select a Chit</option>
          {chitList.map((chit) => (
            <option key={chit._id} value={chit.chit_det.chit_name}>
              {chit.chit_det.chit_name}
            </option>
          ))}
        </select>
        <input
          type="date"
          required
          value={date}
          onChange={(e) => setDate(e.target.value)}
          style={{ width: "150px" }}
        />
        <input
          type="text"
          required
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          placeholder="Amount"
        />
        <button type="submit">Create</button>
      </form>

      <table>
        <thead>
          <tr>
            <th>S.No</th>
            <th>Chit Name</th>
            <th>Total Amount</th>
            <th>Due Amount</th>
            <th>Time Period</th>
            <th>Member Name</th>
            <th>Views</th>
          </tr>
        </thead>
        <tbody>
          {chitMasterList.map((chitMaster, index) => (
            <tr key={chitMaster._id}>
              <td>{index + 1}</td>
              <td>{chitMaster.chit_det && chitMaster.chit_det.chit_name}</td>
              <td>{chitMaster.chit_det && chitMaster.chit_det.Tolal_amount}</td>
              <td>{chitMaster.chit_det && chitMaster.chit_det.Due_amount}</td>
              <td>{chitMaster.chit_det && chitMaster.chit_det.time_preiod}</td>
              <td>
                {chitMaster.member_list &&
                  chitMaster.member_list.map((member, index) => (
                    <div key={index}>{member.name}</div>
                  ))}
              </td>
              <td>
                <a
                  href={`/views/${
                    chitMaster.chit_det && chitMaster.chit_det.chit_name
                  }`}
                >
                  Views
                </a>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ChitMaster;
