import React, { useEffect, useState } from "react";
import axios from "axios";
import { chit_get_url, chitdue_get_url, chitdue_add_url, due } from "./url/url";

const ChitMaster = () => {
  const [chitMasterList, setChitMasterList] = useState([]);
  const [chitList, setChitList] = useState([]);
  const [selectedChit, setSelectedChit] = useState("");
  const [date, setDate] = useState("");
  const [amount, setAmount] = useState("");
  const [list, SetList] = useState("");

  useEffect(() => {
    fetchChitMasterList();
    fetchChitList();
  }, []);

  const fetchChitMasterList = async () => {
    try {
      const response = await axios.get(chitdue_get_url);
      setChitMasterList(response.data);
    } catch (error) {
      console.error("Error fetching chit master list:", error);
    }
  };

  const fetchChitList = async () => {
    try {
      const response = await axios.get(chit_get_url);
      setChitList(response.data);
    } catch (error) {
      console.error("Error fetching chit list:", error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const dueLists = [];

      const chitDet = {
        chit_name: selectedChit,
        date: date,
        amount: amount,
        member: dueLists,
      };

      chitList.forEach((chit) => {
        if (chit.chit_det && chit.chit_det.chit_name === selectedChit) {
          chit.member_list.forEach((member) => {
            const dueList = {
              name: member.name,
              date: date,
              amount: amount,
              pending_flag: false,
            };
            dueLists.push(dueList);
          });
        }
      });

      await updateChitDue(chitDet);
    } catch (error) {
      console.error("Error fetching chit master list:", error);
    }
  };

  const updateChitDue = async (dueLists) => {
    try {
      await axios.post(due, dueLists);
      console.log("Chit dues updated successfully!");
    } catch (error) {
      console.error("Error updating chit dues:", error);
    }
  };

  return (
    <div>
      <form
        onSubmit={handleSubmit}
        style={{ display: "flex", flexDirection: "row" }}
      >
        <select
          value={selectedChit}
          onChange={(e) => setSelectedChit(e.target.value)}
        >
          <option value="">Select a Chit</option>
          {chitList.map((chit) => (
            <option key={chit._id} value={chit.chit_det.chit_name}>
              {chit.chit_det.chit_name}
            </option>
          ))}
        </select>
        <input
          type="date"
          required
          value={date}
          onChange={(e) => setDate(e.target.value)}
          style={{ width: "150px" }}
        />
        <input
          type="text"
          required
          value={list}
          onChange={(e) => SetList(e.target.value)}
          style={{ width: "150px" }}
        />
        <input
          type="text"
          required
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          placeholder="Amount"
        />
        <button type="submit">Create</button>
      </form>

      <table>
        <thead>
          <tr>
            <th>S.No</th>
            <th>Chit Name</th>
            <th>Total Amount</th>
            <th>Due Amount</th>
            <th>Time Period</th>
            <th>Member Name</th>
            <th>Views</th>
          </tr>
        </thead>
        <tbody>
          {chitMasterList.map((chitMaster, index) => (
            <tr key={chitMaster._id}>
              <td>{index + 1}</td>
              <td>{chitMaster.chit_det && chitMaster.chit_det.chit_name}</td>
              <td>{chitMaster.chit_det && chitMaster.chit_det.Tolal_amount}</td>
              <td>{chitMaster.chit_det && chitMaster.chit_det.Due_amount}</td>
              <td>{chitMaster.chit_det && chitMaster.chit_det.time_preiod}</td>
              <td>
                {chitMaster.member_list &&
                  chitMaster.member_list.map((member, index) => (
                    <div key={index}>{member.name}</div>
                  ))}
              </td>
              <td>
                <a
                  href={`/views/${
                    chitMaster.chit_det && chitMaster.chit_det.chit_name
                  }`}
                >
                  Views
                </a>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ChitMaster;
