import React, { useState, useEffect } from "react";
import Modal from "./Modal"; // Assuming you have a Modal component
import axios from "axios";
import { useForm, useFieldArray } from "react-hook-form";
import { chit_get_url, chit_add_url, customer_get_url } from "./url/url";

const Add_chit = () => {
  const [showModal, setShowModal] = useState(false);
  const { register, control, handleSubmit } = useForm({
    defaultValues: {
      chit_det: {
        chit_name: "",
        Tolal_amount: "",
        Due_amount: "",
        time_preiod: "",
      },
      member_list: [{ name: "" }],
    },
  });
  const { fields, append, remove } = useFieldArray({
    control,
    name: "member_list",
  });
  const [customerList, setCustomerList] = useState([]);
  const [chitList, setChitList] = useState([]);

  useEffect(() => {
    fetchCustomerList();
    fetchChitList();
  }, []);

  const fetchCustomerList = async () => {
    try {
      const res = await axios.get(customer_get_url);
      setCustomerList(res.data);
    } catch (error) {
      console.error("Error fetching customer list:", error);
    }
  };

  const fetchChitList = async () => {
    try {
      const res = await axios.get(chit_get_url);
      setChitList(res.data);
    } catch (error) {
      console.error("Error fetching chit list:", error);
    }
  };

  const onSubmit = async (data) => {
    try {
      const requestData = {
        chit_det: data.chit_det,
        member_list: data.member_list,
      };
      const response = await axios.post(chit_add_url, requestData);
      console.log("Server Response:", response.data);
      // Close modal after submission
      setShowModal(false);
    } catch (error) {
      console.error("Error while submitting data:", error);
    }
  };

  const onAddProduct = () => {
    append({ name: "" });
  };

  const openModal = () => {
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
  };

  return (
    <div>
      <button onClick={openModal}>Add Chit</button>

      {showModal && (
        <Modal onClose={closeModal}>
          <form onSubmit={handleSubmit(onSubmit)}>
            <div className="row">
              <div className="col-6">
                <div>
                  <label>Chit Name:</label>
                  <input
                    {...register("chit_det.chit_name")}
                    placeholder="Chit Name"
                  />
                </div>
                <div>
                  <label>Chit Amount:</label>
                  <input
                    {...register("chit_det.Tolal_amount")}
                    placeholder="Total Amount"
                  />
                </div>
                <div>
                  <label>Due Amount:</label>
                  <input
                    {...register("chit_det.Due_amount")}
                    placeholder="Due Amount"
                  />
                </div>
                <div>
                  <label>Time Period:</label>
                  <input
                    {...register("chit_det.time_preiod")}
                    placeholder="Time Period"
                  />
                </div>
              </div>

              <div className="col-6">
                <table>
                  <tbody>
                    {fields.map((item, index) => (
                      <tr key={item.id}>
                        <td>
                          <input
                            {...register(`member_list.${index}.name`)}
                            placeholder="Member Name"
                            list={`customer-list-${index}`}
                          />
                          <datalist id={`customer-list-${index}`}>
                            {customerList.map((option, optionIndex) => (
                              <option key={optionIndex} value={option.name}>
                                {option.name}
                              </option>
                            ))}
                          </datalist>
                        </td>
                        <td>
                          <button type="button" onClick={() => remove(index)}>
                            Remove
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
                <button type="button" onClick={onAddProduct}>
                  Add Member
                </button>
              </div>
            </div>
            <button type="submit">Submit</button>
          </form>
        </Modal>
      )}

      {/* Display Chit List */}
      <div>
        <h2>Chit List</h2>
        <table>
          <thead>
            <tr>
              <th>Chit Name</th>
              <th>Total Amount</th>
              <th>Due Amount</th>
              <th>Time Period</th>
              <th>Members</th>
            </tr>
          </thead>
          <tbody>
            {chitList.map((chit, index) => (
              <tr key={index}>
                <td>{chit.chit_det.chit_name}</td>
                <td>{chit.chit_det.Tolal_amount}</td>
                <td>{chit.chit_det.Due_amount}</td>
                <td>{chit.chit_det.time_preiod}</td>
                <td>
                  <ul>
                    {chit.member_list.map((member, memIndex) => (
                      <li key={memIndex}>{member.name}</li>
                    ))}
                  </ul>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Add_chit;
