import axios from "axios"
import React, { useEffect, useState } from "react"
import { URL_CUS } from "../url"



const Coustomer = () => {
const [cuslist, setCuslist] = useState([])
const [name, setName] =useState('')
const [pone, setPhone] =useState('')
const [pending_flag, setPending_flag] =useState('')

const handleSubmit=()=>{
    setName("");
    setPhone("");
    setPending_flag("");
    add_cus_post();
    cus_get_list();
}
const add_cus_post = async()=>{
    await axios.post(URL_CUS,{
        name,pone,pending_flag
    })
}
  useEffect(() => {
    cus_get_list()
  }, [])

  const cus_get_list = async () => {
    try {
      const res = await axios.get(URL_CUS)
      console.log(res.data)
      // Assuming your response contains an array of customers
      setCuslist(res.data)
    } catch (error) {
      console.error("Error fetching customer list:", error)
    }
  }

  return (
    <div>
        <form onSubmit={handleSubmit}>
        <label>name</label><input type="text" required onChange={(e)=>setName(e.target.value)}></input>
        <label>Phone</label><input type="Phone" required onChange={(e)=>setPhone(e.target.value)}></input>
        <label>Penting</label><input type="text" required onChange={(e)=>setPending_flag(e.target.value)}></input>
        <button>Add</button>
    </form>
    <table>
      <thead>
        <tr>
          <th>S.No</th>
          <th>Name</th>
          <th>Phone</th>
          <th>pending_flag</th>
          <th>Views</th>
        </tr>
      </thead>
      <tbody>
        {cuslist.map((customer, index) => (
          <tr key={index}>
            <td>{index + 1}</td>
            <td>{customer.name}</td>
            <td>{customer.pone}</td>
            <td>{customer.pending_flag}</td>
            <td><a href="/viwes">Views</a></td>
          </tr>
        ))}
      </tbody>
    </table>
    </div>
  )
}

export default Coustomer
