import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  //   chit_get_url,
  //   chitdue_get_url,
  //   chitdue_add_url,
  //   chitdue_amount_update_url,
  dueamountunwind_url,
} from "./url/url";

const Chit = () => {
  const [chitduememberlist, setChitduememberlist] = useState([]);

  useEffect(() => {
    getchitduememberlist();
  }, []);

  const getchitduememberlist = async () => {
    try {
      const response = await axios.get(dueamountunwind_url);
      setChitduememberlist(response.data);
    } catch (error) {
      console.error("Error fetching chit master list:", error);
    }
  };

  const handlePaidAmountChange = async (memberId, newPaidAmount) => {
    try {
      // Assuming you have an API endpoint to update the paid amount
      await axios.put(`/api/members/${memberId}/updatePaidAmount`, {
        paidAmount: newPaidAmount,
      });
      // Optionally, you can update the local state with the new data from the backend
      // getchitduememberlist();
    } catch (error) {
      console.error("Error updating paid amount:", error);
    }
  };

  return (
    <div>
      <h5>Due List</h5>
      <table>
        <thead>
          <tr>
            <th>Member Name</th>
            <th>Chit Name</th>
            <th>Chit List</th>
            <th>Date</th>
            <th>Amount</th>
            <th>Due Amount</th>
            <th>Pending Flag</th>
            <th>Paid Amount</th>
          </tr>
        </thead>
        <tbody>
          {chitduememberlist.map((member, index) => (
            <tr key={index}>
              <td>{member.member.name}</td>
              <td>{member.chit_name}</td>
              <td>{member.chit_list}</td>
              <td>{member.date}</td>
              <td>{member.amount}</td>
              <td>{member.member.due_amount}</td>
              <td>{member.member.pending_flag ? "Yes" : "No"}</td>
              <td>
                <input
                  type="text"
                  value={member.member.paidAmount}
                  onChange={(e) =>
                    handlePaidAmountChange(
                      member.member.member_id,
                      e.target.value
                    )
                  }
                  style={{
                    width: "150px",
                    backgroundColor: member.member.due_amount
                      ? "lightgreen"
                      : "inherit",
                    pointerEvents: member.member.due_amount ? "none" : "auto",
                  }}
                  readOnly={member.member.due_amount ? true : false}
                  placeholder={member.member.due_amount}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Chit;
