import axios from "axios"
import React, { useEffect, useState } from "react"
import { URL_CUS } from "../url"



const ChitMaster = () => {
const [ChitMasterlist, setChitMasterlist] = useState([])
const [name, setName] =useState('')
const [Date, setDate] =useState('')
const [Amount, setAmount] =useState('')

const handleSubmit=(e)=>{
    e.preventDefault();
    setName("");
    setDate("");
    setAmount("");
    add_cus_post();
    cus_get_list();
}
const add_cus_post = async()=>{
    await axios.post(URL_CUS,{
        name,Date,Amount
    })
}
  useEffect(() => {
    cus_get_list()
  }, [])

  const cus_get_list = async () => {
    try {
      const res = await axios.get(URL_CUS)
      console.log(res.data)
      // Assuming your response contains an array of customers
      setChitMasterlist(res.data)
    } catch (error) {
      console.error("Error fetching customer list:", error)
    }
  }

  return (
    <div>
        <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'row' }}>
  <input type="text" required onChange={(e) => setName(e.target.value)} placeholder="name" />
  <input type="date" required onChange={(e) => setDate(e.target.value)} readOnly />
  <input type="text" required onChange={(e) => setAmount(e.target.value)} placeholder="Amount" />
  <button>Create</button>
</form>
    <table>
      <thead>
        <tr>
          <th>S.No</th>
          <th>Name</th>
          <th>Phone</th>
          <th>pending_flag</th>
          <th>Views</th>
        </tr>
      </thead>
      <tbody>
        {ChitMasterlist.map((customer, index) => (
          <tr key={index}>
            <td>{index + 1}</td>
            <td>{customer.name}</td>
            <td>{customer.pone}</td>
            <td>{customer.pending_flag}</td>
            <td><a href="/viwes">Views</a></td>
          </tr>
        ))}
      </tbody>
    </table>
    </div>
  )
}

export default ChitMaster
