import React from 'react';
import './App.css';
import Add_cus from './components/add_cus';
import Coustomer from './components/coustomer';
import { useState } from 'react';

function App() {
  const [triggerUpdate, setTriggerUpdate] = useState(false);

  // Function to toggle the state and trigger updates
  const handleUpdate = () => {
    setTriggerUpdate(!triggerUpdate);
  };
  return (
    <div>
      <Add_cus onAddCustomer={handleUpdate} />
      <Coustomer triggerUpdate={triggerUpdate} />
    </div>
  );
}

export default App;
