import axios from "axios";
import { useForm, useFieldArray } from "react-hook-form";
import { chit_get_url, chit_add_url } from "./url/url";
import { useEffect, useState } from "react";

const Add_chit = () => {
  const { register, control, handleSubmit } = useForm({
    defaultValues: {
      chit_det: {
        chit_name: "",
        Tolal_amount: "",
        Due_amount: "",
        time_preiod: "",
      },
      member_list: [{ name: "" }],
    },
  });

  const { fields, append, remove } = useFieldArray({
    control,
    name: "member_list",
  });

  const [memberList, setMemberList] = useState([]);

  useEffect(() => {
    const fetchMemberList = async () => {
      try {
        const res = await axios.get(chit_get_url);
        setMemberList(res.data.member_list); // Assuming member_list is an array of objects with 'name' property
        console.log(res.data);
      } catch (error) {
        console.error("Error fetching member list:", error);
      }
    };

    fetchMemberList();
  }, []);

  const onSubmit = async (data) => {
    console.log(data);

    try {
      const response = await axios.post(chit_add_url, data);
      console.log("Server Response:", response.data);
    } catch (error) {
      console.error("Error while submitting data:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <div className="row">
        <div className="col-6">
          <div>
            <label>Chit Name:</label>
            <input
              {...register("chit_det.chit_name")}
              placeholder="Chit Name"
            />
          </div>
          <div>
            <label>Chit Amount:</label>
            <input
              {...register("chit_det.Tolal_amount")}
              placeholder="Total Amount"
            />
          </div>
          <div>
            <label>Due Amount:</label>
            <input
              {...register("chit_det.Due_amount")}
              placeholder="Due Amount"
            />
          </div>
          <div>
            <label>Time Period:</label>
            <input
              {...register("chit_det.time_preiod")}
              placeholder="Time Period"
            />
          </div>
        </div>

        <div className="col-6">
          <table>
            <tbody>
              {fields.map((item, index) => (
                <tr key={item.id}>
                  <td>
                    <input
                      {...register(`member_list.${index}.name`)}
                      placeholder="Member Name"
                      list={`datalist-${index}`}
                    />
                    <datalist id={`datalist-${index}`}>
                      {memberList.map((option, optionIndex) => (
                        <option key={optionIndex} value={option.name}>
                          {option.name}
                        </option>
                      ))}
                    </datalist>
                  </td>
                  <td>
                    <button type="button" onClick={() => remove(index)}>
                      Remove
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <button type="button" onClick={() => append({ name: "" })}>
            Add Member
          </button>
        </div>
      </div>
      <button type="submit">Submit</button>
    </form>
  );
};

export default Add_chit;
