import React, { useState } from "react";

const Accountdata = ({ chitData }) => {
  const [filters, setFilters] = useState({
    memberName: "",
    fromDate: "",
    toDate: "",
    minCredit: "",
    maxCredit: "",
    minDebit: "",
    maxDebit: "",
  });

  // Function to handle changes in filter inputs
  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters((prevFilters) => ({
      ...prevFilters,
      [name]: value,
    }));
  };

  // Function to filter chit data based on selected filters
  const filteredChitData = chitData.filter((chit) => {
    return (
      (filters.memberName === "" ||
        chit.memberName
          .toLowerCase()
          .includes(filters.memberName.toLowerCase())) &&
      (filters.fromDate === "" ||
        new Date(chit.date.$date.$numberLong) >= new Date(filters.fromDate)) &&
      (filters.toDate === "" ||
        new Date(chit.date.$date.$numberLong) <= new Date(filters.toDate)) &&
      (filters.minCredit === "" ||
        parseFloat(chit.credit) >= parseFloat(filters.minCredit)) &&
      (filters.maxCredit === "" ||
        parseFloat(chit.credit) <= parseFloat(filters.maxCredit)) &&
      (filters.minDebit === "" ||
        parseFloat(chit.debit) >= parseFloat(filters.minDebit)) &&
      (filters.maxDebit === "" ||
        parseFloat(chit.debit) <= parseFloat(filters.maxDebit))
    );
  });

  return (
    <div>
      {/* Filter inputs */}
      <input
        type="text"
        name="memberName"
        value={filters.memberName}
        onChange={handleFilterChange}
        placeholder="Member Name"
      />
      <input
        type="date"
        name="fromDate"
        value={filters.fromDate}
        onChange={handleFilterChange}
      />
      <input
        type="date"
        name="toDate"
        value={filters.toDate}
        onChange={handleFilterChange}
      />
      <input
        type="number"
        name="minCredit"
        value={filters.minCredit}
        onChange={handleFilterChange}
        placeholder="Min Credit"
      />
      <input
        type="number"
        name="maxCredit"
        value={filters.maxCredit}
        onChange={handleFilterChange}
        placeholder="Max Credit"
      />
      <input
        type="number"
        name="minDebit"
        value={filters.minDebit}
        onChange={handleFilterChange}
        placeholder="Min Debit"
      />
      <input
        type="number"
        name="maxDebit"
        value={filters.maxDebit}
        onChange={handleFilterChange}
        placeholder="Max Debit"
      />

      {/* Display filtered data */}
      <table>
        <thead>
          <tr>
            <th>Member Name</th>
            <th>Date</th>
            <th>Credit</th>
            <th>Debit</th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          {filteredChitData.map((chit, index) => (
            <tr key={index}>
              <td>{chit.memberName}</td>
              <td>
                {new Date(chit.date.$date.$numberLong).toLocaleDateString()}
              </td>
              <td>{chit.credit}</td>
              <td>{chit.debit}</td>
              <td>{chit.description}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Accountdata;
