import React, { useEffect, useState } from "react";
import axios from "axios";
import { URL_CUS } from "../url";

const Coustomer = () => {
  const [cuslist, setCuslist] = useState([]);
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');
  const [pending_flag, setPending_flag] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setName("");
    setPhone("");
    setPending_flag("");
    await add_cus_post();
    await cus_get_list();
  };

  const add_cus_post = async () => {
    try {
      await axios.post(URL_CUS, { name, phone, pending_flag });
      console.log('Customer added successfully.');
    } catch (error) {
      console.error('Error adding customer:', error.message);
    }
  };

  const cus_get_list = async () => {
    try {
      const res = await axios.get(URL_CUS);
      console.log(res.data);
      setCuslist(res.data);
    } catch (error) {
      console.error("Error fetching customer list:", error);
    }
  };

  useEffect(() => {
    cus_get_list();
  }, []);

  return (
    <div>
      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'row' }}>
        <input type="text" required onChange={(e) => setName(e.target.value)} placeholder="name" />
        <input type="Phone" required onChange={(e) => setPhone(e.target.value)} placeholder="Phone" />
        <input type="text" required onChange={(e) => setPending_flag(e.target.value)} placeholder="flag" />
        <button>Add</button>
      </form>
      <table>
        <thead>
          <tr>
            <th>S.No</th>
            <th>Name</th>
            <th>Phone</th>
            <th>pending_flag</th>
            <th>Views</th>
          </tr>
        </thead>
        <tbody>
          {cuslist.map((customer, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{customer.name}</td>
              <td>{customer.phone}</td>
              <td>{customer.pending_flag}</td>
              <td><a href="/views">Views</a></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Coustomer;
