import React, { useState } from 'react';
import './App.css';
import Add_cus from './components/add_cus';
import Coustomer from './components/coustomer';

function App() {
  const [customerList, setCustomerList] = useState([]);

  const addCustomerToList = (newCustomer) => {
    setCustomerList((prevList) => [...prevList, newCustomer]);
  };

  return (
    <div>
      <Add_cus onAddCustomer={addCustomerToList} />
      <Coustomer cuslist={customerList} />
    </div>
  );
}

export default App;
