import axios from "axios";
import React, { useEffect, useState } from "react";
import { chitdue_add_url, chit_get_url, chitdue_get_url } from "./url/url";

const ChitMaster = () => {
  const [chitMasterList, setChitMasterList] = useState([]);
  const [FilterchitMasterList, setFilterChitMasterList] = useState([]);
  const [FilterchitMasterLista, setFilterChitMasterLista] = useState({
    chit_det: { chit_name: "", date: "", amount: "" },
    Member_list: [{ Member_name: "", amount: "", pending_flag: "" }],
  });
  const [name, setName] = useState("");
  const [date, setDate] = useState("");
  const [amount, setAmount] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.get(`${chit_get_url}?${name}`);
      setFilterChitMasterList(response.data);

      const dueLists = [];
      const chit_det = { chit_name: name, date: date, amount: amount };

      response.data.forEach((item) => {
        item.member_list.forEach((member) => {
          const dueList = {
            chit_det: chit_det,
            name: member.name,
            date: date,
            amount: amount,
            pending_flag: "true",
          };
          dueLists.push(dueList);
        });
      });

      setFilterChitMasterLista(dueLists);
      console.log(FilterchitMasterLista);
    } catch (error) {
      console.error("Error fetching chit master list:", error);
    }
  };

  useEffect(() => {
    // Fetch initial data when the component mounts
    getChitMasterList();
  }, []);

  const getChitMasterList = async () => {
    try {
      const response = await axios.get(URL_CUS_chit);
      setChitMasterList(response.data);
    } catch (error) {
      console.error("Error fetching chit master list:", error);
    }
  };

  return (
    <div>
      <form
        onSubmit={handleSubmit}
        style={{ display: "flex", flexDirection: "row" }}
      >
        <input
          type="text"
          required
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Name"
        />
        <input
          type="date"
          required
          value={date}
          onChange={(e) => setDate(e.target.value)}
          style={{ width: "150px" }}
        />
        <input
          type="text"
          required
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          placeholder="Amount"
        />
        <button type="submit">Create</button>
      </form>

      <table>
        <thead>
          <tr>
            <th>S.No</th>
            <th>Name</th>
            <th>Phone</th>
            <th>Pending Flag</th>
            <th>Views</th>
          </tr>
        </thead>
        <tbody>
          {chitMasterList.map((chitMaster, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{chitMaster.chit_det.chit_name}</td>
              <td>{chitMaster.chit_det.Tolal_amount}</td>
              <td>{chitMaster.chit_det.time_preiod}</td>
              <td>
                <a href={`/views/${chitMaster.chit_det.chit_name}`}>Views</a>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ChitMaster;
