import React, { useState } from "react";
import axios from "axios";
import { URL_CUS } from "../url";

const Add_cus = ({ onAddCustomer }) => {
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [pending_flag, setPending_flag] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post(URL_CUS, {
        name,
        phone,
        pending_flag,
      });

      const newCustomer = response.data; // Assuming the response contains the new customer data
      onAddCustomer(newCustomer);
      
      setName("");
      setPhone("");
      setPending_flag("");
    } catch (error) {
      console.error("Error adding customer:", error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>Name</label>:{" "}
      <input
        type="text"
        onChange={(e) => setName(e.target.value)}
        value={name}
      ></input>
      <label>Phone</label>:{" "}
      <input
        type="text"
        onChange={(e) => setPhone(e.target.value)}
        value={phone}
      ></input>
      <label>Pending</label>:{" "}
      <input
        type="text"
        onChange={(e) => setPending_flag(e.target.value)}
        value={pending_flag}
      ></input>
      <button>Add</button>
    </form>
  );
};

export default Add_cus;
