import axios from "axios"
import React, { useEffect, useState } from "react"
import { URL_CUS } from "../url"
import Add_cus from "./add_cus"


const Coustomer = () => {
  const [cuslist, setCuslist] = useState([])

  useEffect(() => {
    cus_get_list()
  }, [])

  const cus_get_list = async () => {
    try {
      const res = await axios.get(URL_CUS)
      console.log(res.data)
      // Assuming your response contains an array of customers
      setCuslist(res.data)
    } catch (error) {
      console.error("Error fetching customer list:", error)
    }
  }

  const handleAddCustomer = () => {
    cus_get_list();
  };

  return (
    
    <div>
      <Add_cus onAddCustomer={handleAddCustomer} />
    <table>
      <thead>
        <tr>
          <th>S.No</th>
          <th>Name</th>
          <th>Phone</th>
          <th>pending_flag</th>
          <th>Views</th>
        </tr>
      </thead>
      <tbody>
        {cuslist.map((customer, index) => (
          <tr key={index}>
            <td>{index + 1}</td>
            <td>{customer.name}</td>
            <td>{customer.pone}</td>
            <td>{customer.pending_flag}</td>
            <td><a href="/viwes">Views</a></td>
          </tr>
        ))}
      </tbody>
    </table>
    </div>
  )
}

export default Coustomer
