import axios from "axios";
import React, { useEffect, useState } from "react";
import { URL_CUS } from "../url";

const ChitMaster = () => {
  const [chitMasterList, setChitMasterList] = useState([]);
  const [name, setName] = useState('');
  const [date, setDate] = useState('');
  const [amount, setAmount] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    async (name) => {
    try {
      const response = await axios.get(URL_CUS/'name');
      setChitMasterList(response.data);
    } catch (error) {
      console.error("Error fetching customer list:", error);
    }
  };
  
    
  };



  useEffect(() => {
  }, []);

  return (
    <div>
      <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'row' }}>
        <input type="text" required value={name} onChange={(e) => setName(e.target.value)} placeholder="Name" />
        <input type="date" required value={date} onChange={(e) => setDate(e.target.value)} style={{ width: '150px' }} />
        <input type="text" required value={amount} onChange={(e) => setAmount(e.target.value)} placeholder="Amount" />
        <button type="submit">Create</button>
      </form>

      <table>
        <thead>
          <tr>
            <th>S.No</th>
            <th>Name</th>
            <th>Phone</th>
            <th>Pending Flag</th>
            <th>Views</th>
          </tr>
        </thead>
        <tbody>
          {chitMasterList.map((customer, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{customer.name}</td>
              <td>{customer.phone}</td>
              <td>{customer.pending_flag}</td>
              <td><a href="/views">Views</a></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ChitMaster;
