import React, { useEffect, useState } from "react";
import axios from "axios";
import { chitdue_amount_update_url, dueamountunwind_url } from "./url/url";

const Chit = () => {
  const [chitduememberlist, setChitduememberlist] = useState([]);
  const [filteredChitduememberlist, setFilteredChitduememberlist] = useState(
    []
  );
  const [filters, setFilters] = useState({
    name: "",
    chitName: "",
    date: "",
    pendingFlag: "",
  });
  const [paidAmounts, setPaidAmounts] = useState({}); // Object to store paid amounts for each member

  useEffect(() => {
    getchitduememberlist();
  }, []);

  useEffect(() => {
    applyFilters();
  }, [chitduememberlist, filters]);

  const getchitduememberlist = async () => {
    try {
      const response = await axios.get(dueamountunwind_url);
      setChitduememberlist(response.data);
      // Initialize paid amounts object with default values
      const initialPaidAmounts = response.data.reduce((acc, member) => {
        acc[member.member.member_id] = member.member.due_amount || ""; // Default value can be due amount or empty string
        return acc;
      }, {});
      setPaidAmounts(initialPaidAmounts);
    } catch (error) {
      console.error("Error fetching chit master list:", error);
    }
  };

  const applyFilters = () => {
    let filteredList = chitduememberlist.filter((member) => {
      return (
        member.member.name.toLowerCase().includes(filters.name.toLowerCase()) &&
        member.chit_name
          .toLowerCase()
          .includes(filters.chitName.toLowerCase()) &&
        member.date.includes(filters.date) &&
        (filters.pendingFlag === "" ||
          member.member.pending_flag === (filters.pendingFlag === "Yes"))
      );
    });
    setFilteredChitduememberlist(filteredList);
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters({ ...filters, [name]: value });
  };

  const handleUpdate = async (chitId, updatedAmount, memberId) => {
    try {
      const payload = {
        id: chitId,
        amount: updatedAmount,
        memberId: memberId,
      };
      const response = await axios.post(chitdue_amount_update_url, payload);
      console.log(response.data); // Log the response from the serverless function
    } catch (error) {
      console.error("Error updating member:", error);
    }
  };

  const handlePaidAmountChange = (memberId, amount) => {
    setPaidAmounts((prev) => ({
      ...prev,
      [memberId]: amount,
    }));
  };

  return (
    <div>
      <h5>Due List</h5>
      <div>
        <input
          type="text"
          name="name"
          placeholder="Filter by Name"
          value={filters.name}
          onChange={handleFilterChange}
        />
        <input
          type="text"
          name="chitName"
          placeholder="Filter by Chit Name"
          value={filters.chitName}
          onChange={handleFilterChange}
        />
        <input
          type="text"
          name="date"
          placeholder="Filter by Date"
          value={filters.date}
          onChange={handleFilterChange}
        />
        <select
          name="pendingFlag"
          onChange={handleFilterChange}
          value={filters.pendingFlag}
        >
          <option value="">All</option>
          <option value="Yes">Pending</option>
          <option value="No">Not Pending</option>
        </select>
      </div>
      <table>
        <thead>
          <tr>
            <th>Member Name</th>
            <th>Chit Name</th>
            <th>Chit List</th>
            <th>Date</th>
            <th>Amount</th>
            <th>Due Amount</th>
            <th>Pending Flag</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {filteredChitduememberlist.map((member, index) => (
            <tr key={index}>
              <td>{member.member.name}</td>
              <td>{member.chit_name}</td>
              <td>{member.chit_list}</td>
              <td>{member.date}</td>
              <td>{member.amount}</td>
              <td>
                <input
                  type="text"
                  value={paidAmounts[member.member.member_id]}
                  onChange={(e) =>
                    handlePaidAmountChange(
                      member.member.member_id,
                      e.target.value
                    )
                  }
                  style={{
                    width: "150px",
                    backgroundColor: member.member.due_amount
                      ? "lightgreen"
                      : "inherit",
                    pointerEvents: member.member.due_amount ? "none" : "auto",
                  }}
                  readOnly={member.member.due_amount ? true : false}
                  placeholder={member.member.due_amount}
                />
              </td>
              <td>{member.member.pending_flag ? "Yes" : "No"}</td>

              <td>
                <button
                  onClick={() =>
                    handleUpdate(
                      member._id,
                      paidAmounts[member.member.member_id],
                      member.member.member_id
                    )
                  }
                >
                  Update
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Chit;
