import React, { useEffect, useState } from "react";
import axios from "axios";
import { chit_get_url, chitdue_get_url, chitdue_add_url, due } from "./url/url";

const ChitMaster = () => {
  const [chitMasterList, setChitMasterList] = useState([]);
  const [chitList, setChitList] = useState([]);
  const [selectedChit, setSelectedChit] = useState("");
  const [date, setDate] = useState("");
  const [amount, setAmount] = useState("");
  const [list, SetList] = useState("");
  const [memberDetails, setMemberDetails] = useState(null); // State to store member details

  useEffect(() => {
    fetchChitMasterList();
    fetchChitList();
  }, []);

  const fetchChitMasterList = async () => {
    try {
      const response = await axios.get(chitdue_get_url);
      setChitMasterList(response.data);
    } catch (error) {
      console.error("Error fetching chit master list:", error);
    }
  };

  const fetchChitList = async () => {
    try {
      const response = await axios.get(chit_get_url);
      setChitList(response.data);
    } catch (error) {
      console.error("Error fetching chit list:", error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const dueLists = [];

      const chitDet = {
        chit_name: selectedChit,
        chit_list: list,
        date: date,
        amount: amount,
        member: dueLists,
      };

      chitList.forEach((chit) => {
        if (chit.chit_det && chit.chit_det.chit_name === selectedChit) {
          chit.member_list.forEach((member) => {
            const dueList = {
              name: member.name,
              date: date,
              amount: amount,
              due_amount: "",
              pending_flag: false,
            };
            dueLists.push(dueList);
          });
        }
      });

      await updateChitDue(chitDet);
    } catch (error) {
      console.error("Error fetching chit master list:", error);
    }
  };

  const updateChitDue = async (dueLists) => {
    try {
      await axios.post(chitdue_add_url, dueLists);
      console.log("Chit dues updated successfully!");
    } catch (error) {
      console.error("Error updating chit dues:", error);
    }
  };

  const handleViewMembers = (members, index) => {
    setChitMasterList(
      chitMasterList.map((chitDue, i) => {
        if (i === index) {
          return { ...chitDue, showDetails: !chitDue.showDetails };
        }
        return chitDue;
      })
    );
  };

  return (
    <div>
      <form
        onSubmit={handleSubmit}
        style={{ display: "flex", flexDirection: "row" }}
      >
        <select
          value={selectedChit}
          onChange={(e) => setSelectedChit(e.target.value)}
        >
          <option value="">Select a Chit</option>
          {chitList.map((chit) => (
            <option key={chit._id} value={chit.chit_det.chit_name}>
              {chit.chit_det.chit_name}
            </option>
          ))}
        </select>
        <input
          type="date"
          required
          value={date}
          onChange={(e) => setDate(e.target.value)}
          style={{ width: "150px" }}
        />
        <input
          type="text"
          required
          value={list}
          onChange={(e) => SetList(e.target.value)}
          style={{ width: "150px" }}
          placeholder="list"
        />
        <input
          type="text"
          required
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          placeholder="Amount"
        />
        <button type="submit">Create</button>
      </form>
      <table>
        <thead>
          <tr>
            <th>Chit Name</th>
            <th>Date</th>
            <th>Amount</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {chitMasterList.map((chitDue, index) => (
            <React.Fragment key={index}>
              <tr>
                <td>{chitDue.chit_name}</td>
                <td>{chitDue.date}</td>
                <td>{chitDue.amount}</td>
                <td>
                  <button
                    onClick={() => handleViewMembers(chitDue.member, index)}
                  >
                    View
                  </button>
                </td>
              </tr>
              {chitDue.showDetails && (
                <tr key={`details_${index}`}>
                  <td colSpan="4">
                    <div>
                      <h6>Member Details</h6>
                      <table>
                        <thead>
                          <tr>
                            <th>Name</th>
                            <th>Date</th>
                            <th>Amount</th>
                            <th>Paid Amount</th>
                            <th>Pending Flag</th>
                            <th>Actions</th>
                          </tr>
                        </thead>
                        <tbody>
                          {chitDue.member.map((member, i) => (
                            <tr key={i}>
                              <td>{member.name}</td>
                              <td>{member.date}</td>
                              <td>{member.amount}</td>
                              <td>
                                <input type="text" />
                              </td>
                              <td>{member.pending_flag.toString()}</td>
                              <td>
                                <button>update</button>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  </td>
                </tr>
              )}
            </React.Fragment>
          ))}
        </tbody>
      </table>
      {/* Member details display */}
      {/* {memberDetails && (
        <div>
          <h6>Member Details</h6>
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Date</th>
                <th>Amount</th>
                <th>dues</th>
                <th>Pending Flag</th>
              </tr>
            </thead>
            <tbody>
              {memberDetails.map((member, index) => (
                <form>
                  <tr key={index}>
                    <td>{member.name}</td>
                    <td>{member.date}</td>
                    <td>{member.amount}</td>
                    <td>
                      <input
                        type="text"
                        required
                        style={{ width: "150px" }}
                        placeholder={member.amount}
                      />
                    </td>
                    <td>{member.pending_flag.toString()}</td>
                    <td>
                      <button>ok</button>
                    </td>
                  </tr>
                </form>
              ))}
            </tbody>
          </table>
        </div>
      )} */}
    </div>
  );
};

export default ChitMaster;
