import axios from "axios";
import React, { useEffect, useState } from "react";
import { chit_get_url, chitdue_get_url } from "./url/url";

const ChitMaster = () => {
  const [chitMasterList, setChitMasterList] = useState([]);
  const [chitList, setChitList] = useState([]);
  const [selectedChit, setSelectedChit] = useState("");
  const [name, setName] = useState("");
  const [date, setDate] = useState("");
  const [amount, setAmount] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Your form submission logic
    } catch (error) {
      console.error("Error fetching chit master list:", error);
    }
  };

  const chitlistget = async () => {
    const response = await axios.get(chit_get_url);
    setChitList(response.data);
    console.log(response.data);
  };

  useEffect(() => {
    getChitMasterList();
    chitlistget();
  }, []);

  const getChitMasterList = async () => {
    try {
      const response = await axios.get(chitdue_get_url);
      setChitMasterList(response.data);
    } catch (error) {
      console.error("Error fetching chit master list:", error);
    }
  };

  return (
    <div>
      <form
        onSubmit={handleSubmit}
        style={{ display: "flex", flexDirection: "row" }}
      >
        <select
          value={selectedChit}
          onChange={(e) => setSelectedChit(e.target.value)}
        >
          <option value="">Select a Chit</option>
          {chitList.map((chit) => (
            <option key={chit._id} value={chit.chit_det.chit_name}>
              {chit.chit_det.chit_name}
            </option>
          ))}
        </select>
        <input
          type="date"
          required
          value={date}
          onChange={(e) => setDate(e.target.value)}
          style={{ width: "150px" }}
        />
        <input
          type="text"
          required
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          placeholder="Amount"
        />
        <button type="submit">Create</button>
      </form>

      <table>
        <thead>
          <tr>
            <th>S.No</th>
            <th>Name</th>
            <th>Phone</th>
            <th>Pending Flag</th>
            <th>Views</th>
          </tr>
        </thead>
        <tbody>
          {chitMasterList.map((chitMaster, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{chitMaster.chit_det.chit_name}</td>
              <td>{chitMaster.chit_det.Tolal_amount}</td>
              <td>{chitMaster.chit_det.time_preiod}</td>
              <td>
                <a href={`/views/${chitMaster.chit_det.chit_name}`}>Views</a>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ChitMaster;
