import axios from 'axios';
import { useForm, useFieldArray } from 'react-hook-form';
import { URL_CUS } from '../url';
import { useEffect, useState } from 'react';

const Add_chit = () => {
  const { register, control, handleSubmit } = useForm({
    defaultValues: {
      chit_det: { chit_name: '', Tolal_amount: '', Due_amount: '', time_preiod: '' },
      member_list: [{ name: '' }],
    },
  });

  const { fields, append, remove } = useFieldArray({
    control,
    name: 'member_list',
  });

  const [list, setCuslist] = useState([]);

  const datalistId = (index) => `datalist-${index}`;

  const member_list = async () => {
    try {
      const res = await axios.get(URL_CUS);
      setCuslist(res.data);
      console.log(res.data);
    } catch (error) {
      console.error('Error fetching customer list:', error);
    }
  };

  useEffect(() => {
    member_list();
  }, []);

  const onSubmit = (data) => {
    console.log(data);
  };

  const onAddProduct = () => {
    append({ name: '' });
  };

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      {/* ... (your other form fields) */}

      <div className="col-2">
        <table>
          <tbody>
            {fields.map((item, index) => (
              <tr key={item.id}>
                <td>
                  <input
                    {...register(`member_list.${index}.name`)}
                    placeholder="Member Name"
                    list={datalistId(index)}
                  />
                  <datalist id={datalistId(index)}>
                    {list.map((option, optionIndex) => (
                      <option key={optionIndex} value={option.name}>
                        {option.name}
                      </option>
                    ))}
                  </datalist>
                </td>
                <td>
                  <button type="button" onClick={() => remove(index)}>
                    Remove
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <button type="button" onClick={onAddProduct}>
          Add Member
        </button>
      </div>

      <button type="submit">Submit</button>
    </form>
  );
};

export default Add_chit;
