import React, { useEffect, useState } from "react";
import axios from "axios";
import { getaccount_url } from "../url/url";

const Accountdata = () => {
  const [chitData, setChitData] = useState([]);
  const [filteredChitData, setFilteredChitData] = useState([]);
  const [filters, setFilters] = useState({
    memberName: "",
    fromDate: "",
    toDate: "",
    minCredit: "",
    maxCredit: "",
    minDebit: "",
    maxDebit: "",
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get(getaccount_url);
      setChitData(response.data); // Assuming the data structure is an array
      setFilteredChitData(response.data); // Initially set filtered data to all data
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    applyFilters();
  }, [chitData, filters]); // Whenever chitData or filters change, reapply filters

  const applyFilters = () => {
    const filteredData = chitData.filter((chit) => {
      return (
        (filters.memberName === "" ||
          chit.memberName
            .toLowerCase()
            .includes(filters.memberName.toLowerCase())) &&
        (filters.fromDate === "" ||
          new Date(chit.date.$date.$numberLong) >=
            new Date(filters.fromDate)) &&
        (filters.toDate === "" ||
          new Date(chit.date.$date.$numberLong) <= new Date(filters.toDate)) &&
        (filters.minCredit === "" ||
          parseFloat(chit.credit) >= parseFloat(filters.minCredit)) &&
        (filters.maxCredit === "" ||
          parseFloat(chit.credit) <= parseFloat(filters.maxCredit)) &&
        (filters.minDebit === "" ||
          parseFloat(chit.debit) >= parseFloat(filters.minDebit)) &&
        (filters.maxDebit === "" ||
          parseFloat(chit.debit) <= parseFloat(filters.maxDebit))
      );
    });
    setFilteredChitData(filteredData);
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters((prevFilters) => ({
      ...prevFilters,
      [name]: value,
    }));
  };

  return (
    <div>
      {/* Filter inputs */}
      <input
        type="text"
        name="memberName"
        value={filters.memberName}
        onChange={handleFilterChange}
        placeholder="Member Name"
      />
      <input
        type="date"
        name="fromDate"
        value={filters.fromDate}
        onChange={handleFilterChange}
      />
      <input
        type="date"
        name="toDate"
        value={filters.toDate}
        onChange={handleFilterChange}
      />
      <input
        type="number"
        name="minCredit"
        value={filters.minCredit}
        onChange={handleFilterChange}
        placeholder="Min Credit"
      />
      <input
        type="number"
        name="maxCredit"
        value={filters.maxCredit}
        onChange={handleFilterChange}
        placeholder="Max Credit"
      />
      <input
        type="number"
        name="minDebit"
        value={filters.minDebit}
        onChange={handleFilterChange}
        placeholder="Min Debit"
      />
      <input
        type="number"
        name="maxDebit"
        value={filters.maxDebit}
        onChange={handleFilterChange}
        placeholder="Max Debit"
      />

      {/* Display filtered data */}
      <table>
        <thead>
          <tr>
            <th>Member Name</th>
            <th>Date</th>
            <th>Credit</th>
            <th>Debit</th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          {filteredChitData.map((chit, index) => (
            <tr key={index}>
              <td>{chit.memberName}</td>
              <td>
                {new Date(chit.date.$date.$numberLong).toLocaleDateString()}
              </td>
              <td>{chit.credit}</td>
              <td>{chit.debit}</td>
              <td>{chit.description}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Accountdata;
