import axios from 'axios';
import { useForm, useFieldArray } from 'react-hook-form';
import { URL_CUS } from '../url';
import { useEffect, useState } from "react"



const Add_chit =()=>{
    
    const { register, control, handleSubmit,reset,setValue} =useForm({
        defaultValues:{
            chit_det: { chit_name: '', Tolal_amount: '',Due_amount:'', time_preiod: '' },
            member_list:[{name:''}]
        }
        
    })
    const { fields, append, remove } = useFieldArray({
        control,
        name: 'member_list',
      });
      const onAddProduct = () => {
        append({ name: ''});
      };
  
  const onSubmit=()=>{

  }
  const datalistId = `datalist-${index}`;
const [list, setCuslist] = useState([])

const member_list=async()=>{
try{
    const res =await axios.get(URL_CUS)
    setCuslist(res.data)
    console.log(res.data)

}catch (error) {
    console.error("Error fetching customer list:", error)
  }
}

useEffect(() => {
    member_list()
  }, [])

  return(
    <form onSubmit={handleSubmit(onSubmit)}>
      <div className='row' >
        <div className='col-4'>
          <div>
            <label>Chit Name:</label>
            <input {...register('chit_det.chit_name')} placeholder="Chit Name" />
          </div>
          <div>
            <label>Chit Amount:</label>
            <input {...register('chit_det.Tolal_amount')} placeholder="Total Amount" type="email" />
          </div>
          <div>
            <label>Due Amount:</label>
            <input {...register('chit_det.Due_amount')} placeholder="Due Amount" />
          </div>
          <div>
            <label>Time Preiod:</label>
            <input {...register('chit_det.time_preiod')} placeholder="Time Preiod" />
          </div>
        </div>
        <div className='col-2' >
        <table>
        
        <tbody>
          <tr>
        {fields.map((item, index) => (
          
          <div key={item.id}>
            <td>
            <input
              {...register(`member_list.${index}.name`)}
              placeholder="Member Name"
              list={datalistId}
            />
             <datalist id={datalistId}>
                {list.map((option, optionIndex) => (
                <option key={optionIndex} value={option.name}>
                {option.name}
          </option>
        ))}
      </datalist>
            </td>
            <td>
            <button type="button" onClick={() => remove(index)}>
              Remove
            </button>
            </td>
            
          </div>
        ))}
        </tr>
        </tbody>
        </table>
        <button type="button" onClick={onAddProduct}>
          Add Member
        </button>
        </div>
      </div>

      <button type="submit">Submit</button>      
    </form>
  )
}
export  default Add_chit
