import axios from "axios"
import React, { useEffect, useState } from "react"
import { URL_CUS } from "../url"



const Coustomer = () => {
// State to manage the selected products
const [selectedProducts, setSelectedProducts] = useState([]);
  
// Dummy product data (replace this with your actual product data)
const products = [
  { id: 1, name: 'Product A', price: 10 },
  { id: 2, name: 'Product B', price: 15 },
  { id: 3, name: 'Product C', price: 20 },
];

// Function to add a product to the selectedProducts array
const addProduct = (productId) => {
  const selectedProduct = products.find(product => product.id === productId);
  setSelectedProducts([...selectedProducts, selectedProduct]);
};

// Function to calculate the total price
const calculateTotal = () => {
  return selectedProducts.reduce((total, product) => total + product.price, 0);
};

return (
  <div>
    <h1>Billing Page</h1>
    
    <div>
      <h2>Products</h2>
      <ul>
        {products.map(product => (
          <li key={product.id}>
            {product.name} - ${product.price} 
            <button onClick={() => addProduct(product.id)}>Add</button>
          </li>
        ))}
      </ul>
    </div>

    <div>
      <h2>Selected Products</h2>
      <ul>
        {selectedProducts.map(product => (
          <li key={product.id}>
            {product.name} - ${product.price}
          </li>
        ))}
      </ul>
      <p>Total: ${calculateTotal()}</p>
    </div>
  </div>
);
}

export default Coustomer
